mapper.select:
		select recruit_no, title, field, location, age, start_date, end_date,to_char(start_date,'YYYY-MM-DD') as check_date
		from(select recruit_no, title, field,location, age,start_date, end_date,to_char(start_date,'HH24:MI') as startDate,to_char(end_date,'HH24:MI') as endDate from recruit order by recruit_no desc)
		
			
				AND field=#{field}
			
			
				AND location=#{location}
			
			
  			
   			 AND startDate>=#{startDate} 
    		 
			
			
  			
   			 AND endDate<=#{endDate}

		select recruit_no, title, field, location, age, start_date, end_date
		from(select recruit_no, title, field, location, age, start_date,
		end_date,
		ceil(rownum/5) as page from(select recruit_no, title, field,
		location, age,
		start_date, end_date from recruit
		
			
				AND field=#{field}
			
			
				AND location=#{location}
			
			
  			
   			 AND to_char(start_date,'YYYY-MM-DD')>=#{startDate} 
    		 
			
			
  			
   			 AND to_char(end_date,'YYYY-MM-DD')<=#{endDate}
    		 
			
		
		order by recruit_no desc) )
		where page=#{pageNo}

		select count(*) from recruit
		
			
				AND field=#{field}
			
			
				AND location=#{location}
			
			
  			
   			 AND start_date>=#{startDate} 
    		 
			
			
  			
   			 AND end_date<=#{endDate}

		select distinct(to_char(start_date,'YYYY-MM-DD')) as date_list from (
		select start_date,to_char(start_date,'HH24:MI') as startDate,to_char(end_date,'HH24:MI') as endDate,field,location from recruit)
		
			
				AND field=#{field}
			
			
				AND location=#{location}
			
			
  			
   			 AND startDate>=#{startDate} 
    		 
			
			
  			
   			 AND endDate<=#{endDate}
    		 
			
		
		order by date_list asc
